package com.example.notification;

import android.content.Context;

/**
 * Created by liuxy on 16/10/25.
 */

public class RNNotificationAndroidHelper {

    private static RNNotificationAndroidHelper instance;

    private RNNotificationAndroidHelper(){

    }

    void clearNotification(Context ctx) {
        RNNotificationService service = RNNotificationService.getInstance(ctx);
        if (service != null) {
            service.clearNotification();
        }
    }

    int getApplicationIconBadgeNumber(Context ctx) {
        RNNotificationService service = RNNotificationService.getInstance(ctx);
        if (service != null) {
            return service.getNum();
        }
        return -1;
    }

    void showNotification(Context ctx,String message) {
        RNNotificationService service = RNNotificationService.getInstance(ctx);
        if (service != null) {
            service.showNotification(message);
        }
    }

    void deleteNotification(Context ctx,int id) {
        RNNotificationService service = RNNotificationService.getInstance(ctx);
        if (service != null) {
            service.deleteNotification(id);
        }
    }

    void onClickNotification(){
        RNNotificationAndroid.onClickNotification();
    }

    static RNNotificationAndroidHelper getInstance(){
        if (instance == null){
            synchronized (RNNotificationAndroidHelper.class){
                if (instance == null){
                    instance = new RNNotificationAndroidHelper();
                }
            }
        }
        return instance;
    }

}
