import {
    NativeModules,
    DeviceEventEmitter,
    NativeAppEventEmitter,
    Platform
} from 'react-native'

const RNNotificationAndroid = NativeModules.RNNotificationAndroid;

export default class RNNotification {

    onNotificationClicked(callback) {
        DeviceEventEmitter.addListener("onNotificationClick", () => {
            callback();
        });
    }

    showNotification(message) {
        if (Platform.OS == 'android') {
            RNNotificationAndroid.showNotification(message);
        }
    }
    clearNotification() {
        if (Platform.OS == 'android') {
            RNNotificationAndroid.clearNotification();
        }
    }
    getApplicationIconBadgeNumber(callback) {
        if (Platform.OS == 'android') {
            RNNotificationAndroid.getApplicationIconBadgeNumber(callback);
        }
    }
}