package com.example.pushlibrary;

import android.content.Intent;

import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.modules.core.DeviceEventManagerModule;

import javax.annotation.Nullable;

public class RNPushAndroid extends ReactContextBaseJavaModule {
    private static final String TAG = "RNPushAndroid";

    ReactApplicationContext mContext;

    public RNPushAndroid(ReactApplicationContext reactContext) {
        super(reactContext);
        mContext = reactContext;
        Intent i=new Intent(mContext,RNPushService.class);
        mContext.startService(i);
    }

    @Override
    public String getName() {
        return TAG;
    }

    static void sendEvent(ReactContext reactContext,
                          String eventName,
                          @Nullable WritableMap params) {
        reactContext
                .getJSModule(DeviceEventManagerModule.RCTDeviceEventEmitter.class)
                .emit(eventName, params);
    }
}