package com.example.pushlibrary;

import com.example.notification.RNNotificationAndroid;
import com.facebook.react.LazyReactPackage;
import com.facebook.react.bridge.JavaScriptModule;
import com.facebook.react.bridge.ModuleSpec;
import com.facebook.react.bridge.NativeModule;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.uimanager.ViewManager;

import java.util.Arrays;
import java.util.Collections;
import java.util.List;

import javax.inject.Provider;

public class RNPushAndroidPackage extends LazyReactPackage {

    @Override
    public List<ModuleSpec> getNativeModules(final ReactApplicationContext reactContext) {
        return Arrays.asList(
                new ModuleSpec(RNNotificationAndroid.class, new Provider<NativeModule>() {
                    @Override
                    public NativeModule get() {
                        return new RNNotificationAndroid(reactContext);
                    }
                }),
                new ModuleSpec(RNPushAndroid.class, new Provider<NativeModule>() {
                    @Override
                    public NativeModule get() {
                        return new RNPushAndroid(reactContext);
                    }
                }));
    }

    @Override
    public List<Class<? extends JavaScriptModule>> createJSModules() {
        return Collections.emptyList();
    }

    @Override
    public List<ViewManager> createViewManagers(ReactApplicationContext reactContext) {
        return Collections.emptyList();
    }
}
